(function () {
    'use strict';

    angular
        .module('app')
        .controller('toastr', toastr);

    toastr.$inject = ['$location'];

    function toastr($location) {
        /* jshint validthis:true */
        var vm = this;
        vm.title = 'toastr';

        activate();

        function activate() { }
    }
})();
